var fs = require('fs');
var http = require('http');

// Serve client side statically
var express = require('express');
var app = express();
app.use(express.static(__dirname + '/public'));

var server = http.createServer(app);

// Start Binary.js server
var BinaryServer = require('../../').BinaryServer;
var bs = BinaryServer({server: server});

// Wait for new user connections
bs.on('connection', function(client){
  client.on('stream', function(stream, meta){

  	var ruta = "/var/www/html/stream/".concat(meta.size);
  	  	console.log(ruta.concat(".xtc"));
  	var file = fs.createReadStream(ruta.concat(".xtc"));
  	client.send(file); 
    //
  });
});
//
//

server.listen(9000);
console.log('HTTP and BinaryJS server started on port 9000');
